  }) {
    __expose();
    const user = ref('');
    const options = reactive([]);
    const account = sessionStorage.getItem("Account");
    const ws = new WebSocket("ws://192.168.1.11:1113/webSocket/" + account);
    let message = reactive({
      fromWho: "",
      toWho: "",
      text: "",
      date: new Date()
    });
