import { ElMessage } from "element-plus";
import { onMounted, reactive, ref } from "vue";
export default {
  __name: 'ChatRoom',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const user = ref('');
    const inputName = ref('');
    const account = sessionStorage.getItem("Account");
    const ws = new WebSocket("ws://192.168.1.11:1113/webSocket/" + account);
