      console.log("connect");
    };
    const fasong = () => {
      if (!inputName.value) ElMessage.info("ddd");
      message.fromWho = account;
      message.text = "hello";
      do {
        message.toWho = inputName.value;
      } while (message.toWho);
      message.date = new Date();
      ws.send(JSON.stringify(message));
      console.log(message);
    };
    ws.onmessage = function (evt) {
      message = JSON.parse(evt.data);
      //console.log(message)
      if (message.fromWho == "system_connect_open") {
        ElMessage.info(message.text + "上线了");
        user.value = message.text;
      }
    };
    ws.onclose = function (evt) {
      console.log("close");
    };
    const __returned__ = {
      user,
      inputName,
      account,
      ws,
      get message() {
        return message;
      },
      set message(v) {
        message = v;
      },
      fasong,
      get router() {
        return router;
      },
      get ElMessage() {
        return ElMessage;
      },
      onMounted,
      reactive,
      ref
    };
